package com.gd.entity;

public class Admin {
	private int admi_id;
	private String admi_name;
	private String admi_pass;
	public Admin() {
		super();
		// TODO Auto-generated constructor stub
	}
	public int getAdmi_id() {
		return admi_id;
	}
	public void setAdmi_id(int admi_id) {
		this.admi_id = admi_id;
	}
	public String getAdmi_name() {
		return admi_name;
	}
	public void setAdmi_name(String admi_name) {
		this.admi_name = admi_name;
	}
	public String getAdmi_pass() {
		return admi_pass;
	}
	public void setAdmi_pass(String admi_pass) {
		this.admi_pass = admi_pass;
	}
	@Override
	public String toString() {
		return "Admin [admi_id=" + admi_id + ", admi_name=" + admi_name
				+ ", admi_pass=" + admi_pass + "]";
	}
	
}
